import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import MealPlan from './MealPlan';
import RatingForm from './RatingForm';

const MealPage = ({ userId, goal, onLogout }) => {
  const [mealPlan, setMealPlan] = useState(null);
  const [day, setDay] = useState(1);

  useEffect(() => {
    fetchMealPlan();
  }, []);

  const fetchMealPlan = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/getMealPlan', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId, goal }),
      });
      const data = await response.json();
      setMealPlan(data);
    } catch (error) {
      console.error('Failed to fetch meal plan:', error);
    }
  };

  const handleRatingSubmit = async (ratings) => {
    try {
      const response = await fetch('http://localhost:5001/api/updateRatings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId, ratings }),
      });

      if (response.ok) {
        setDay(prev => prev + 1);
        fetchMealPlan();
      }
    } catch (error) {
      console.error('Failed to submit ratings:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Day {day}</h1>
        <div className="flex space-x-4">
          <Link 
            to="/nutrition" 
            className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
          >
            Nutrition Analysis
          </Link>
          <button
            onClick={onLogout}
            className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
          >
            Logout
          </button>
        </div>
      </div>

      <div className="max-w-2xl mx-auto">
        {mealPlan && (
          <>
            <MealPlan mealPlan={mealPlan} />
            <RatingForm mealPlan={mealPlan} onSubmitRatings={handleRatingSubmit} />
          </>
        )}
      </div>
    </div>
  );
};

export default MealPage;
