import React, { useState } from 'react';

const MealPlan = ({ mealPlan }) => {
  const [selectedRecipeUrl, setSelectedRecipeUrl] = useState(null);

  const openRecipeModal = (url) => {
    setSelectedRecipeUrl(url);
  };

  const closeRecipeModal = () => {
    setSelectedRecipeUrl(null);
  };

  return (
    <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
      <h2 className="text-xl font-bold mb-4">Today's Meal Plan</h2>
      {Object.entries(mealPlan).map(([meal, { name, url }]) => (
        <div key={meal} className="mb-4 pb-2 border-b">
          <h3 className="font-bold text-gray-700">
            {meal.charAt(0).toUpperCase() + meal.slice(1)}:
          </h3>
          <p className="text-gray-600">{name}</p>
          {/* Button to open recipe modal */}
          {url && (
            <button 
              className="mt-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
              onClick={() => openRecipeModal(url)}
            >
              View Recipe
            </button>
          )}
        </div>
      ))}

      {/* Recipe Modal */}
      {selectedRecipeUrl && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center">
          <div className="bg-white rounded-lg shadow-xl w-11/12 max-w-4xl h-5/6 relative">
            <div className="flex justify-between items-center p-4 border-b">
              <h2 className="text-xl font-bold">Recipe Details</h2>
              <button 
                onClick={closeRecipeModal}
                className="text-gray-600 hover:text-gray-900"
              >
                âœ•
              </button>
            </div>
            <div className="w-full h-[calc(100%-60px)]">
              <iframe 
                src={selectedRecipeUrl}
                className="w-full h-full"
                title="Recipe Details"
                allowFullscreen
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MealPlan;
