import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogClose } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { X } from 'lucide-react';

const MealPlan = ({ mealPlan }) => {
  const [selectedRecipeUrl, setSelectedRecipeUrl] = useState(null);

  const openRecipeModal = (url) => {
    setSelectedRecipeUrl(url);
  };

  const closeRecipeModal = () => {
    setSelectedRecipeUrl(null);
  };

  return (
    <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
      <h2 className="text-xl font-bold mb-4">Today's Meal Plan</h2>
      {Object.entries(mealPlan).map(([meal, { name, url }]) => (
        <div key={meal} className="mb-4 pb-2 border-b">
          <h3 className="font-bold text-gray-700">
            {meal.charAt(0).toUpperCase() + meal.slice(1)}:
          </h3>
          <p className="text-gray-600">{name}</p>
          {/* Button to open recipe modal */}
          {url && (
            <Button 
              variant="outline" 
              className="mt-2"
              onClick={() => openRecipeModal(url)}
            >
              View Recipe
            </Button>
          )}
        </div>
      ))}

      {/* Recipe Modal */}
      <Dialog open={!!selectedRecipeUrl} onOpenChange={closeRecipeModal}>
        <DialogContent className="max-w-4xl h-[80vh] p-0">
          <DialogHeader className="p-4 pb-0">
            <DialogTitle>Recipe Details</DialogTitle>
            <DialogClose className="absolute right-4 top-4" onClick={closeRecipeModal}>
              <X className="h-4 w-4" />
            </DialogClose>
          </DialogHeader>

          {selectedRecipeUrl && (
            <iframe 
              src={selectedRecipeUrl}
              className="w-full h-[calc(80vh-60px)]"
              title="Recipe Details"
            />
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default MealPlan;
